CREATE TABLE IF NOT EXISTS Campaign(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT);
CREATE TABLE IF NOT EXISTS Character(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT, CampaignCampaign REAL, CurrentSizeDomainSize REAL, SpeciesDomainSpecies REAL, SubclassDomainSubClass REAL, CONSTRAINT fk_Campaign FOREIGN KEY (CampaignCampaign) REFERENCES Campaign(Id), CONSTRAINT fk_DomainSize FOREIGN KEY (CurrentSizeDomainSize) REFERENCES DomainSize(Id), CONSTRAINT fk_DomainSpecies FOREIGN KEY (SpeciesDomainSpecies) REFERENCES DomainSpecies(Id), CONSTRAINT fk_DomainSubClass FOREIGN KEY (SubclassDomainSubClass) REFERENCES DomainSubClass(Id));
CREATE TABLE IF NOT EXISTS DomainAction(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT, UsesAction NUMBER, UsesBonusAction NUMBER);
CREATE TABLE IF NOT EXISTS DomainCharacterStat(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT, UsesAction NUMBER, UsesBonusAction NUMBER);
CREATE TABLE IF NOT EXISTS DomainClass(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT, HitDieDomainDice REAL, SpellcastingStatDomainCharacterStat REAL, CONSTRAINT fk_DomainDice FOREIGN KEY (HitDieDomainDice) REFERENCES DomainDice(Id), CONSTRAINT fk_DomainCharacterStat FOREIGN KEY (SpellcastingStatDomainCharacterStat) REFERENCES DomainCharacterStat(Id));
CREATE TABLE IF NOT EXISTS DomainCondition(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT);
CREATE TABLE IF NOT EXISTS DomainCreatureType(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT);
CREATE TABLE IF NOT EXISTS DomainDamageType(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT);
CREATE TABLE IF NOT EXISTS DomainDice(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Maximum REAL, Minimum REAL, Title TEXT);
CREATE TABLE IF NOT EXISTS DomainDiceRollType(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT);
CREATE TABLE IF NOT EXISTS DomainItem(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, CustomEffectText TEXT, Description TEXT, IsActive NUMBER, OneHandedQuantifier REAL, Title TEXT, TwoHandedQuantifier REAL);
CREATE TABLE IF NOT EXISTS DomainSize(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, BaseHexArea REAL, BaseTileArea REAL, Description TEXT, IsActive NUMBER, SizeOrder REAL, Title TEXT);
CREATE TABLE IF NOT EXISTS DomainSpecies(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT, CreatureTypeDomainCreatureType REAL, CONSTRAINT fk_DomainCreatureType FOREIGN KEY (CreatureTypeDomainCreatureType) REFERENCES DomainCreatureType(Id));
CREATE TABLE IF NOT EXISTS DomainSpell(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, ConcentrationRequired NUMBER, Description TEXT, HourCastTime REAL, HourDuration REAL, IsAction NUMBER, IsActive NUMBER, IsBonusAction NUMBER, IsCantrip NUMBER, IsMelee NUMBER, IsRanged NUMBER, IsRitual NUMBER, Level REAL, MaterialComponent TEXT, MinuteCastTime REAL, RoundDuration REAL, SomaticComponent TEXT, Title TEXT, VerbalComponent TEXT, DamageScalingDomainDice REAL, SchoolDomainSpellSchool REAL, CONSTRAINT fk_DomainDice FOREIGN KEY (DamageScalingDomainDice) REFERENCES DomainDice(Id), CONSTRAINT fk_DomainSpellSchool FOREIGN KEY (SchoolDomainSpellSchool) REFERENCES DomainSpellSchool(Id));
CREATE TABLE IF NOT EXISTS DomainSpellSchool(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT);
CREATE TABLE IF NOT EXISTS DomainStaticEffect(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT);
CREATE TABLE IF NOT EXISTS DomainSubClass(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Description TEXT, IsActive NUMBER, Title TEXT, ParentClassDomainClass REAL, CONSTRAINT fk_DomainClass FOREIGN KEY (ParentClassDomainClass) REFERENCES DomainClass(Id));
CREATE TABLE IF NOT EXISTS Quantifier(Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Delta REAL, Description TEXT, ImpactsSelf NUMBER, IntoInventory NUMBER, IsAction NUMBER, IsActive NUMBER, IsBonusAction NUMBER, Level1SpellSlots REAL, Level2SpellSlots REAL, Level3SpellSlots REAL, Level4SpellSlots REAL, Level5SpellSlots REAL, Level6SpellSlots REAL, Level7SpellSlots REAL, Level8SpellSlots REAL, Level9SpellSlots REAL, LevelMaximumRequirement REAL, LevelMinimumRequirement REAL, Quantity REAL, RefreshOnLongRest NUMBER, RefreshOnShortRest NUMBER, Title TEXT, UntilLongRest NUMBER, UntilShortRest NUMBER, AddedSpellDomainSpell REAL, ConditionDomainCondition REAL, DamageTypeDomainDamageType REAL, EffectDomainStaticEffect REAL, ResistanceTypeDomainDamageType REAL, SaveDomainCharacterStat REAL, TargetDomainCharacterStat REAL, CONSTRAINT fk_DomainSpell FOREIGN KEY (AddedSpellDomainSpell) REFERENCES DomainSpell(Id), CONSTRAINT fk_DomainCondition FOREIGN KEY (ConditionDomainCondition) REFERENCES DomainCondition(Id), CONSTRAINT fk_DomainDamageType FOREIGN KEY (DamageTypeDomainDamageType) REFERENCES DomainDamageType(Id), CONSTRAINT fk_DomainStaticEffect FOREIGN KEY (EffectDomainStaticEffect) REFERENCES DomainStaticEffect(Id), CONSTRAINT fk_DomainDamageType FOREIGN KEY (ResistanceTypeDomainDamageType) REFERENCES DomainDamageType(Id), CONSTRAINT fk_DomainCharacterStat FOREIGN KEY (SaveDomainCharacterStat) REFERENCES DomainCharacterStat(Id), CONSTRAINT fk_DomainCharacterStat FOREIGN KEY (TargetDomainCharacterStat) REFERENCES DomainCharacterStat(Id));
